//   console.log('resss',response)
//         setHotels(response.data.data);
// }

const [location, setLocation] = useState('');
  const [hotels, setHotels] = useState([]);
console.log('hotels',hotels)
  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:4000/v1//user/hotels?query=${location}`);
      setHotels(response.data);
    } catch (error) {
      console.error(error);
    }
  };




  return (
    <div className="App">
    <h1>Hotel Search App</h1>
    <div>
      
      <input type="text" />
    </div>
    <button onClick={handleSearch}>Search Hotels</button>
    <ul>
      {hotels.map((hotel) => (
        <li key={hotel.properties.xid}>
          <h3>{hotel.properties.name}</h3>
          {hotel.properties.preview ? (
            <img src={hotel.properties.preview.source} alt={hotel.properties.name} />
          ) : (
            <p>No image available</p>
          )}
          <p>Address: {hotel.properties.address}</p>
        </li>
      ))}
       </ul>
    </div>
  );
};

export default HotelList;
