import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

class HotelMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPosition: null,
      hotelLocation: { lat: '36.92388507655352', lng: '75.3565647949157' },
    };
  }
  componentDidMount() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        position => {
          const { latitude, longitude } = position.coords;
          this.setState({
            currentPosition: { lat: latitude, lng: longitude },
          });
        },
        error => {
          console.error(error);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }
  calculateDistance = () => {
    console.log('running')
    const { currentPosition, hotelLocation } = this.state;
    const { google } = this.props; // Access the google prop
  
    if (currentPosition && google) {
      const distance = google.maps.geometry.spherical.computeDistanceBetween(
        new google.maps.LatLng(currentPosition.lat, currentPosition.lng),
        new google.maps.LatLng(hotelLocation.lat, hotelLocation.lng)
      );
      console.log(`Distance to hotel: ${distance} meters`);
    }
  };
  render() {
    return (
      <div>
        <Map
          google={this.props.google}
          initialCenter={this.state.hotelLocation}
          zoom={8} // Adjust this value as needed
          // initialCenter={{
          //   lat: (this.state.currentPosition.lat + this.state.hotelLocation.lat) / 2,
          //   lng: (this.state.currentPosition.lng + this.state.hotelLocation.lng) / 2,
          // }}
        >
          {this.state.currentPosition && (
            <Marker
              position={this.state.currentPosition}
              label="You are here"
            />
          )}
          <Marker
            position={this.state.hotelLocation}
            label="Khunjerab Top"
          />
        </Map>
        <button className='vvv' onClick={this.calculateDistance}>Calculate Distance</button>
      </div>
    );
  }
}
export default GoogleApiWrapper({
  apiKey: 'AIzaSyC6xvlbMFrLYt9ExmJvyFnd5pawC_Al4rs',
})(HotelMap);
