// AddHotelForm.js
import React, { useState } from 'react';
import axios from 'axios';
import './add_hotel.css'; // Import your CSS file

const AddHotelForm = () => {
  const [hotelName, setHotelName] = useState('');
  const [location, setLocation] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
      const response = await axios.post('/api/hotels', {
        name: hotelName,
        location: location,
      });
      
      if (response.status === 201) {
        alert('Hotel added successfully!');
        setHotelName('');
        setLocation('');
      }
    } catch (error) {
      console.error('Error adding hotel:', error);
      alert('An error occurred while adding the hotel.');
    }
  };
  return (
    
    <div className="container">
    <div className="form-container">
      <h2 className="form-title">Add Hotel</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label" htmlFor="name">Hotel Name:</label>
          <input
            type="text"
            id="name"
            className="form-input"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label className="form-label" htmlFor="description">Description:</label>
          <textarea
            id="description"
            className="form-textarea"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          </div>
          <div className="form-group">
            <label className="form-label" htmlFor="image">Hotel Image:</label>
            <input
              type="file"
              id="image"
              accept="image/*"
              onChange={handleImageChange}
            />
          </div>
          <button type="submit" className="form-button">Add Hotel</button>
        </form>
      </div>
    </div>
  );
};

export default AddHotelForm;
