// AddHotelForm.js
import React, { useState } from 'react';
import axios from 'axios';
import './add_hotel.css'; // Import your CSS file

const AddHotelForm = () => {
  const [hotelName, setHotelName] = useState('');
  const [location, setLocation] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
      const response = await axios.post('/api/hotels', {
        name: hotelName,
        location: location,
      });
      
      if (response.status === 201) {
        alert('Hotel added successfully!');
        setHotelName('');
        setLocation('');
      }
    } catch (error) {
      console.error('Error adding hotel:', error);
      alert('An error occurred while adding the hotel.');
    }
  };
  return (
    <div className="add-hotel-container">
      <form className="add-hotel-form" onSubmit={handleSubmit}>
        <h2>Add Hotel</h2>
        <div className="input-container">
          <label className='add_hotel_label' htmlFor="hotelName">Hotel Name</label>
          <input
          className='add_hotel_inp'
            type="text"
            id="hotelName"
            value={hotelName}
            onChange={(e) => setHotelName(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label className='add_hotel_label' htmlFor="location">Location</label>
          <input className='add_hotel_inp'
            type="text"
            id="location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </div>
        <button className='add_hotel_btn' type="submit">Add Hotel</button>
      </form>
    </div>
  );
};

export default AddHotelForm;
