import React, { useState } from 'react';
import axios from 'axios';

const FormComponent = () => {
  const [numForms, setNumForms] = useState(1);
  const [formState, setFormState] = useState([]);

  const handleNumFormsChange = (e) => {
    setNumForms(parseInt(e.target.value));
    setFormState(new Array(parseInt(e.target.value)).fill({ name: '', email: '' }));
  };

  const handleFieldChange = (e, formIndex, fieldName) => {
    const updatedFormState = [...formState];
    updatedFormState[formIndex][fieldName] = e.target.value;
    setFormState(updatedFormState);
  };
  const saveToDatabase = async () => {
    try {
      const response = await axios.post('/api/saveFormData', formState);
      console.log('Data saved:', response.data);
    } catch (error) {
      console.error('Error saving data:', error);
    }
  };

  const renderForms = () => {
    return formState.map((form, index) => (
      <div key={index}>
        <input
          type="text"
          value={form.name}
          onChange={(e) => handleFieldChange(e, index, 'name')}
          placeholder="Name"
        />
        <input
          type="email"
          value={form.email}
          onChange={(e) => handleFieldChange(e, index, 'email')}
          placeholder="Email"
        />
        </div>
    ));
  };

  return (
    <div>
      <label htmlFor="numForms">Number of Forms:</label>
      <input
        type="number"
        id="numForms"
        value={numForms}
        onChange={handleNumFormsChange}
      />
      {renderForms()}
      <button onClick={saveToDatabase}>Save to Database</button>
    </div>
  );
};

export default FormComponent;
