import React, { useEffect, useState } from 'react';
import './destination.css'
import axios from 'axios'
const HotelDestinations = () => {
  const [location, setLocation] = useState('');
  const [hotels, setHotels] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [selectedHotel, setSelectedHotel] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await fetch(`http://localhost:4000/v1/hotel/gethotels?location=${location}`);
      const data = await response.json();
      console.log('data',data)
      setHotels(data);
    } catch (error) {
      console.error('Error searching hotels:', error);
    }

  };


  const togglePopup = (hotel) => {
    setSelectedHotel(hotel); 
    setIsPopupOpen(!isPopupOpen);
  };
    return (
        <div>
      <h2>Hotel Search</h2>
      <input
        type="text"
        placeholder="Enter location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={handleSearch}>Search Hotels</button>
      <div className="hotels-list">
      {hotels.map((hotel, index) => (
        <div className="hotel-card" key={index}>
          <img className="hotel-image" src={hotel.image} alt={hotel.name} />
          <div className="hotel-details">
            <h2 className="hotel-name">{hotel.name}</h2>
            <p className="hotel-location">{hotel.location}</p>
            <p className="hotel-description">{hotel.description}</p>
            <p className="hotel-price">${hotel.price} per night</p>
            <button className="book-button" onClick={() => togglePopup(hotel)}>Book Now</button>
            {isPopupOpen && (
        <div className="booking-popup">
          <h3>Book a Room at {selectedHotel.name}</h3>
          <p>Location: {selectedHotel.location}</p>
          <p>Price: ${hoselectedHoteltel.price} per night</p>
          {/* Add more booking information fields here */}
          <button className="close-popup-button" onClick={togglePopup}>Close</button>
        </div>
      )}
          </div>
        </div>
      ))}
    </div>
    </div>
       
      );  
};

export default HotelDestinations;
