import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, Polyline } from 'google-maps-react';

class KhunjerabMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPosition: null,
      khunjerabTop: { lat: 37.089856, lng: 74.719859 }, // Khunjerab Top coordinates
    };
  }
  componentDidMount() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        position => {
          const { latitude, longitude } = position.coords;
          this.setState({
            currentPosition: { lat: latitude, lng: longitude },
          });
        },
        error => {
          console.error(error);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }
  render() {
    const { google } = this.props;

    return (
      <div>
        <Map
          google={google}
          zoom={6}
          initialCenter={this.state.khunjerabTop}
        >
          {this.state.currentPosition && (
            <>
              <Marker
                position={this.state.currentPosition}
                label="You are here"
              />
              <Polyline
                path={[
                  this.state.currentPosition,
                  this.state.khunjerabTop
                ]}
                geodesic={true}
                options={{
                  strokeColor: "#FF0000",
                  strokeOpacity: 1.0,
                  strokeWeight: 2,
                }}
              />
            </>
          )}
          <Marker
            position={this.state.khunjerabTop}
            label="Khunjerab Top"
          />
        </Map>
      </div>
    );
  }
}
export default GoogleApiWrapper({
  apiKey: 'AIzaSyC6xvlbMFrLYt9ExmJvyFnd5pawC_Al4rs',
})(KhunjerabMap);
