import React, { useState } from 'react';
import axios from 'axios';

const AddMainRoom = () => {
  const [numForms, setNumForms] = useState(1);
  const [formState, setFormState] = useState([]);

  const handleNumFormsChange = (e) => {
    setNumForms(parseInt(e.target.value));
    setFormState(new Array(parseInt(e.target.value)).fill({ name: '', email: '' }));
  };

  const handleFieldChange = (e, formIndex, fieldName) => {
    const updatedFormState = [...formState];
    updatedFormState[formIndex][fieldName] = e.target.value;
    setFormState(updatedFormState);
  };
  const saveToDatabase = async () => {
    try {
      console.log('formstare', formState)
      const response = await axios.post('/api/saveFormData', formState);
      console.log('Data saved:', response.data);
    } catch (error) {
      console.error('Error saving data:', error);
    }
  };

  const renderForms = () => {
    return formState.map((form, index) => (
      <div className="hotel-card" key={index}>
        <div className="hotel-details">
          <div>
            <label htmlFor="roomnumber">Room Number:</label>
            <input
              type="text"
              id="roomnumber"
              name="roomnumber"
              value={form.name}
                 onChange={(e) => handleFieldChange(e, index, 'roomnumber')}
            />
          </div>
          <div>           
 <label htmlFor="roomsize">Room Size:</label>
         <input
           type="text"
           id="roomsize"
          name="roomsize"
          onChange={(e) => handleFieldChange(e, index, 'roomsize')}
          value={form.roomsize}
        />
 </div>

 <div>
 <label htmlFor="roomprice">Price:</label>
        <input
          type="text"
           id="roomprice"
           name="roomprice"
           onChange={(e) => handleFieldChange(e, index, 'roomprice')}
           value={form.roomprice}
        />
 </div>

 <div>
 <label htmlFor="attachbath">BathRoom:</label>
        <input
           type="text"
           id="attachbath"
           name="attachbath"
           onChange={(e) => handleFieldChange(e, index, 'attachbath')}
           value={form.attachbath}


         />
 </div>

 <div>
 <label htmlFor="availability">Availability:</label>
         <input
           type="text"
          id="availability"
          name="availability"
         onChange={(e) => handleFieldChange(e, index, 'availability')}
         value={form.availability}

         />
 </div>

        </div>
      </div>
      // <div key={index}>
      //   <input
      //     type="text"
      //     value={form.name}
      //     onChange={(e) => handleFieldChange(e, index, 'name')}
      //     placeholder="Name"
      //   />
      //   <input
      //     type="email"
      //     value={form.email}
      //     onChange={(e) => handleFieldChange(e, index, 'email')}
      //     placeholder="Email"
      //   />
      //   </div>
    ));
  };

  return (
    <div>
      <label htmlFor="numForms">Number of Forms:</label>
      <input
        type="number"
        id="numForms"
        value={numForms}
        onChange={handleNumFormsChange}
      />
      <div className='main_add_room_container'>

   
       <div className="hotels-list">
       {renderForms()}  
       </div>
     </div>
      <button onClick={saveToDatabase}>Save to Database</button>
    </div>
  );
};

export default AddMainRoom;
