import React, { useEffect, useState } from 'react';
import './nearby.css'
import axios from 'axios'
const HotelDestinations = () => {
  const [location, setLocation] = useState('');
  const [hotels, setHotels] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [selectedHotel, setSelectedHotel] = useState(null);
  const [isBooked, setIsBooked] = useState(false);

  const handleSearch = async () => {
    try {
      const response = await fetch(`http://localhost:4000/v1/hotel/getdestination?location=${location}`);
      const data = await response.json();
      setHotels(data);
      console.log('hotels',hotels)
    } catch (error) {
      console.error('Error searching hotels:', error);
    }

  };

  const [bookingDetails, setBookingDetails] = useState({
    name: '',
    email: '',
    description:'',
    cnic:'',
    number_of_rooms:'',
    number_of_person:'',
    country:'',
    checkInDate: '',
    checkOutDate: '',
    hotelId:''
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingDetails({
      ...bookingDetails,
      [name]: value
    });
  };

  


  const togglePopup = (hotel) => {
    setSelectedHotel(hotel); 
    setIsPopupOpen(!isPopupOpen);
  };
    return (
        <div>
      <h2>Destination Search</h2>
      <input
        type="text"
        placeholder="Enter location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={handleSearch}>Search Your Destinations</button>
      <div className="hotels-list">
      {hotels.map((hotel, index) => (
        <div className="hotel-card" key={index}>
          <img className="hotel-image" src={`http://localhost:4000${hotel.imageUrl}`} alt={hotel.name} width="300" />
          <div className="hotel-details">
            <h2 className="hotel-name">{hotel.name}</h2>
            <p className="hotel-location">{hotel.location}</p>
            <p className="hotel-description">{hotel.description}</p>
            <p className="hotel-price">${hotel.price} per night</p>
          </div>
        </div>
      ))}
    </div>
    </div>
       
      );  
};

export default HotelDestinations;
