import React, { useState } from 'react';
import RoomAddition from './add_room';
import './add_room.css'
import { useLocation } from 'react-router-dom';

const AddMainRoom = () => {
    const [hotelId, sethotelId] = useState('');
    const [hotelname, sethotelname] = useState('');
    const location = useLocation();
    const hotel_details = location.state;
 

  const [rooms, setRooms] = useState([]);


  const handleRoomsAdded = (newRooms) => {
    setRooms([...rooms, ...newRooms]);
  };
  const handleInputChange = (index, field, value) => {
    setFormData((prevFormData) =>
      prevFormData.map((item, i) =>
        i === index ? { ...item, [field]: value } : item
      )
    );
  };
  return (
    <div>
        <RoomAddition onRoomsAdded={handleRoomsAdded} hname={hotel_details.name} />
      <div className='main_add_room_container'>
      <div className="hotels-list">
      {rooms.map((hotel, index) => (
        <div className="hotel-card" key={index}>
         
          <div className="hotel-details">
        <div>
        <label htmlFor="roomnumber">Room Number:</label>
        <input
          type="text"
          id="roomnumber"
          name="roomnumber"
        //   value={userData.firstName}
        onChange={(e) => handleInputChange(index, 'roomnumber', e.target.value)}
        />
        </div>

<div>
<label htmlFor="roomnumber">Room Size:</label>
        <input
          type="text"
          id="roomsize"
          name="roomsize"
          onChange={(e) => handleInputChange(index, 'roomsize', e.target.value)}
        //   value={userData.firstName}
        //   onChange={handleInputChange}
        />
</div>

<div>
<label htmlFor="roomnumber">Price:</label>
        <input
          type="text"
          id="roomprice"
          name="roomprice"
          onChange={(e) => handleInputChange(index, 'roomprice', e.target.value)}
        //   value={userData.firstName}
        //   onChange={handleInputChange}
        />
</div>

<div>
<label htmlFor="roomnumber">BathRoom:</label>
        <input
          type="text"
          id="attachbath"
          name="attachbath"
        //   value={userData.firstName}
        onChange={(e) => handleInputChange(index, 'attachbath', e.target.value)}

        />
</div>

<div>
<label htmlFor="roomnumber">Availability:</label>
        <input
          type="text"
          id="attachbath"
          name="availability"
        //   value={userData.firstName}
        onChange={(e) => handleInputChange(index, 'availability', e.target.value)}

        />
</div>
            
          </div>
        </div>
      ))}
      <button   className='destination_poters'>SAVE</button> 
    </div>
      </div>
    </div>
  );
};

export default AddMainRoom
