import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';

const SuccessPage=()=> {
    const stripe = useStripe();
  const elements = useElements();
  const [clientSecret, setClientSecret] = useState('');
  
  const handleSubmit = async (event) => {
    event.preventDefault();
console.log('banchot',clientSecret)
    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });
    if (result.error) {
        console.error(result.error.message);
      } else {
        if (result.paymentIntent.status === 'succeeded') {
          console.log('Payment successful');
        }
      }
    };
  
    React.useEffect(() => {
        handleCreatePaymentIntent();
      }, []); 
    const handleAmountChange = (event) => {
      // Handle changes in the payment amount
    };
  
    const handleCurrencyChange = (event) => {
      // Handle changes in the currency
    };
  
    const handleCreatePaymentIntent = async () => {
      const response = await axios.post('http://localhost:4000/api/create-payment-intent', {
        amount: 1000, // Amount in cents
        currency: 'usd', // Currency code
      });
      console.log('clientsecrettt',response.data.clientSecret)
      setClientSecret(response.data.clientSecret);
    };

  return (
    <div>
    <form onSubmit={handleSubmit}>
    <input type="number" onChange={handleAmountChange} placeholder="Amount in cents" />
  <input type="text" onChange={handleCurrencyChange} placeholder="Currency code" />
  <CardElement />
  <button type="submit" onClick={handleCreatePaymentIntent} disabled={!stripe}>
    Pay
  </button>
    </form>
  </div>
  )
}

export default SuccessPage;
