import React, { useEffect, useState } from 'react';
import axios from 'axios'
const HotelList = () => {
//   const [hotels, setHotels] = useState([]);
// const changeHandler= async(e)=>{
//   const text=e.target.value;
//   const response = await axios(`http://localhost:4000/v1//user/hotels?query=${text}`);
//   console.log('resss',response)
//         setHotels(response.data.data);
// }

const [location, setLocation] = useState('');
  const [hotels, setHotels] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:4000/v1//user/hotels?query=${location}`);
      setHotels(response.data);
    } catch (error) {
      console.error(error);
    }
  };




  return (
    <div className="App">
    <h1>Hotel Search App</h1>
    <input
      type="text"
      value={location}
      onChange={(e) => setLocation(e.target.value)}
      placeholder="Enter location"
    />
    <button onClick={handleSearch}>Search</button>
    <ul>
      {hotels.map((hotel) => (
        <li key={hotel.properties.xid}>
          {hotel.properties.name} - {hotel.properties.rate} stars
        </li>
      ))}
    </ul>
  </div>
  );
};

export default HotelList;
