import React from 'react';
import { useLocation } from 'react-router-dom';
import Slider from 'react-slick';
import '../hotels/nearby.css'
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';


const PotorPage=()=> {
  const location = useLocation();
  const receivedData = location.state;

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div className="slider-container">

        {receivedData.map((hotel, index) => (
        <div className="hotel-card" key={index}>
          <img className="hotel-image" src={`http://localhost:4000${hotel.imageUrl}`} alt={hotel.name} width="300" />
          <div className="hotel-details">
            <h2 className="hotel-name">{hotel.name}</h2>
            <p className="hotel-location">{hotel.address}</p>
            <p className="hotel-description">{hotel.description}</p>
            <p className="hotel-price">{hotel.rating}</p>
           
          </div>
        </div>
      ))}

    </div>
  );
}

export default PotorPage;
