import React, { useEffect, useState } from 'react';
import axios from 'axios'
const HotelDestinations = () => {
    const [hotels, setHotels] = useState([]);
    const [cityCode, setCityCode] = useState('');
  
    const searchHotels = async () => {
      try {
        const response = await axios.get('/api/searchHotels', {
          params: {
            cityCode: cityCode,
          },
        });
        setHotels(response.data.data);
      } catch (error) {
        console.error(error);
      }
    };

    return (
        <div className="App">
          <h1>Hotel Search App</h1>
          <div>
            <label>City Code:</label>
            <input type="text" value={cityCode} onChange={(e) => setCityCode(e.target.value)} />
          </div>
          <button onClick={searchHotels}>Search Hotels</button>
          <ul>
            {hotels.map((hotel) => (
              <li key={hotel.hotel.hotelId}>
                <h3>{hotel.hotel.name}</h3>
                <p>Price: {hotel.offers[0].price.total}</p>
              </li>
            ))}
          </ul>
        </div>
      );


  
  
};

export default HotelDestinations;
